mapper.insert:
/* H2 Database */
MERGE INTO short_forecast AS target USING (VALUES ( #{nx}, #{ny}, #{baseDate}, #{baseTime}, #{fcstDate}, #{fcstTime}, #{category}, #{fcstValue}, CURRENT_TIMESTAMP )) AS source ( nx, ny, base_date, base_time, fcst_date, fcst_time, category, fcst_value, created_at ) ON target.nx = source.nx AND target.ny = source.ny AND target.fcst_date = source.fcst_date AND target.fcst_time = source.fcst_time AND target.category = source.category WHEN MATCHED THEN UPDATE SET base_date = source.base_date, base_time = source.base_time, fcst_value = source.fcst_value, created_at = source.created_at WHEN NOT MATCHED THEN INSERT (nx, ny, base_date, base_time, fcst_date, fcst_time, category, fcst_value, created_at) VALUES (source.nx, source.ny, source.base_date, source.base_time, source.fcst_date, source.fcst_time, source.category, source.fcst_value, source.created_at)
/* PostgreSQL */
INSERT INTO short_forecast ( nx, ny, base_date, base_time, fcst_date, fcst_time, category, fcst_value, created_at ) VALUES ( #{nx}, #{ny}, #{baseDate}, #{baseTime}, #{fcstDate}, #{fcstTime}, #{category}, #{fcstValue}, CURRENT_TIMESTAMP ) ON CONFLICT (nx, ny, fcst_date, fcst_time, category) DO UPDATE SET base_date = EXCLUDED.base_date, base_time = EXCLUDED.base_time, fcst_value = EXCLUDED.fcst_value, created_at = CURRENT_TIMESTAMP
