mapper.select:

SELECT CATEGORY,
       FCST_VALUE,
       fcst_date + fcst_time + '00' as FCST_DATETIME
  FROM short_forecast sf
 WHERE nx = #{nx}
   AND ny = #{ny}
   AND fcst_value != '-999'
   AND category IN ('TMP', 'SKY', 'POP', 'PCP')
   AND fcst_date + fcst_time BETWEEN FORMAT(GETDATE(), 'yyyyMMddHH') AND FORMAT(DATEADD(DAY, 1, GETDATE()), 'yyyyMMddHH')
ORDER BY fcst_date, fcst_time, category;


SELECT NX, NY, FCST_DATE, FCST_TIME, CATEGORY, FCST_VALUE
  FROM short_forecast 
 WHERE nx = #{nx} 
   AND ny = #{ny}
   AND category IN ('TMP', 'POP', 'REH')
   AND fcst_date = FORMAT(GETDATE(), 'yyyyMMdd')
   AND fcst_time = FORMAT(GETDATE(), 'HH24') + '00';
